package com.ControlStatments_loops;

public class DoWhileDemo {
	         void disp()
	         {
	 	 int no=3;
	        	 do
	        	 {
	        		System.out.println("Hello");
	        		no++;
	        		System.out.println(no);
	        	 }
	        	 while(no<=2);
	         }

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		DoWhileDemo d=new DoWhileDemo();
		d.disp();
	}

}
